// 绘制地月日
import * as THREE from "three"

const scene = new THREE.Scene()

const renderer = new THREE.WebGLRenderer()
renderer.setSize(window.innerWidth, window.innerHeight);
document.body.appendChild(renderer.domElement);


const camera = new THREE.PerspectiveCamera(
    75,
    window.innerWidth / window.innerHeight,
    0.1,
    1000
);
camera.position.z = 5

// 一球多用
const radius = 1;
const widthSegments = 6;
const heightSegments = 6;
const sphereGeometry = new THREE.SphereGeometry(
    radius,
    widthSegments,
    heightSegments
);

const objects = []

const solarSystem = new THREE.Object3D();
scene.add(solarSystem);
objects.push(solarSystem);

const sunMaterial = new THREE.MeshPhongMaterial({ emissive: 0xffff00 });
const sunMesh = new THREE.Mesh(sphereGeometry, sunMaterial);
sunMesh.scale.set(5, 5, 5); // 扩大太阳的大小
solarSystem.add(sunMesh);
objects.push(sunMesh)

// 设置太阳点光源
const color = 0xffffff;
const intensity = 3;
const light = new THREE.PointLight(color, intensity);
scene.add(light);


camera.position.set(0, 50, 0);
camera.up.set(0, 0, 1);
camera.lookAt(0, 0, 0);


const earthmoonSystem = new THREE.Object3D();
earthmoonSystem.position.x = 10;
solarSystem.add(earthmoonSystem);
objects.push(earthmoonSystem);


// 将地球添加进场景
const earthMaterial = new THREE.MeshPhongMaterial({
    color: 0x2233ff,
    emissive: 0x112244,   // 自身放射淡蓝色
});




const earthMesh = new THREE.Mesh(sphereGeometry, earthMaterial);
earthmoonSystem.add(earthMesh);  // 将地球变成太阳的子节点
objects.push(earthMesh);

const moonOrbit = new THREE.Object3D();
moonOrbit.position.x = 3;      // 相对于地球系统而言
earthmoonSystem.add(moonOrbit);
 
const moonMaterial = new THREE.MeshPhongMaterial({color: 0x888888, emissive: 0x222222});
const moonMesh = new THREE.Mesh(sphereGeometry, moonMaterial);
moonMesh.scale.set(.5, .5, .5);
moonOrbit.add(moonMesh);
objects.push(moonMesh);

function render(time) {
    time *= 0.001
    objects.forEach(obj => {
        obj.rotation.y = time
    })
    renderer.render(scene, camera)
    requestAnimationFrame(render)
}

requestAnimationFrame(render)
