// 两个建筑之间的道路
// 加载gltf模型
import * as THREE from 'three';
import { BoxGeometry, BufferGeometry, CylinderGeometry, DirectionalLight, Group, LineBasicMaterial, MeshPhongMaterial, PerspectiveCamera, PlaneGeometry, SphereGeometry, Vector2, Vector3 } from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js'
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls.js';
import { Line2 } from 'three/examples/jsm/lines/Line2.js'
import { LineGeometry } from 'three/examples/jsm/lines/linegeometry.js'
import { LineMaterial } from 'three/examples/jsm/lines/linematerial.js';
const scene = new THREE.Scene()

const renderer = new THREE.WebGLRenderer()
renderer.setSize(window.innerWidth, window.innerHeight);
let c = document.getElementById('c')

c.appendChild(renderer.domElement);
renderer.setClearColor(0xaaaaaa)    // 设置背景色
renderer.shadowMap.enabled = true   // 开启投影
function makeCamera(fov = 40) {
    const aspect = 2
    const zNear = 0.1
    const zFar = 1000
    return new PerspectiveCamera(fov,aspect,zNear,zFar)
}

const camera = makeCamera()
camera.position.set(16,8,20)
camera.lookAt(0,0,0)
// 设置主光
const light = new THREE.DirectionalLight(0xffffff,1) // 平行光 / 正交光
light.position.set(20,20,20)
scene.add(light)
light.castShadow = true // 允许呈现阴影
light.shadow.mapSize.width = 2048   // 设置阴影的分辨率
light.shadow.mapSize.height = 2048
const d = 50
// 设置主光的视域
light.shadow.camera.left = -d
light.shadow.camera.right = d
light.shadow.camera.top = d
light.shadow.camera.bottom = -d
light.shadow.camera.near = 1
light.shadow.camera.far = 50
// 设置辅助光(不投影)
{
    const light = new THREE.DirectionalLight(0xffffff,1)
    light.position.set(1,2,4)
    scene.add(light)
}
// 创建地面
const groundGeometry = new THREE.PlaneGeometry(500, 500)
const groundMaterial = new THREE.MeshPhongMaterial({ color: 0xcc8866 })
const groundMesh = new THREE.Mesh(groundGeometry, groundMaterial)
groundMesh.rotation.x = Math.PI * -0.5
groundMesh.receiveShadow = true
scene.add(groundMesh)

renderer.render(scene, camera);

const loader = new GLTFLoader();

loader.load( './model/building_jiaoxue.gltf', function ( gltf ) {
    gltf.scene.scale.set(0.17,0.17,0.17)
    gltf.scene.position.set(-8,0,0)
    gltf.scene.rotation.y = Math.PI * 0.5
    gltf.scene.castShadow = true
	scene.add( gltf.scene );
})
// building_bangong
loader.load( './model/building_bangong.gltf', function ( gltf ) {
    gltf.scene.scale.set(0.17,0.17,0.17)
    gltf.scene.position.set(50,0,-15)
    gltf.scene.rotation.y = Math.PI * 0.5
    gltf.scene.castShadow = true
	scene.add( gltf.scene );
})
// 绘制道路
const curve = new THREE.SplineCurve([
    new THREE.Vector2(-8,0),
    new THREE.Vector2(-7,5),
    new THREE.Vector2(0,-9),
    new THREE.Vector2(3,9),
    new THREE.Vector2(50,-15),
])
console.log(curve.getLength())
const points = curve.getPoints(50)  // 生成点集

let geometry = new LineGeometry()
let pointArr = []
points.forEach(v => {
    pointArr = pointArr.concat([v.x,0,v.y])
})
geometry.setPositions(pointArr)
let roadmaterial  = new LineMaterial( {
    color: 0xdd2222,
    linewidth: 20,
})
roadmaterial.resolution.set(window.innerWidth,window.innerHeight);
let road = new Line2(geometry, roadmaterial);
scene.add(road)




function render(time) {
    renderer.render(scene, camera);
    requestAnimationFrame(render);
}

let control = new OrbitControls(camera,renderer.domElement);
control.enableRotate =true; //启用旋转
control.enablePan = true; //启用平移
control.enableZoom =true;//启用缩放



requestAnimationFrame(render)
