import * as THREE from 'three';
import * as dat from "dat.gui";
import { BoxGeometry, BufferGeometry, CylinderGeometry, DirectionalLight, Group, LineBasicMaterial, MeshPhongMaterial, PerspectiveCamera, PlaneGeometry, SphereGeometry, Vector2, Vector3 } from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js'

const scene = new THREE.Scene()

const renderer = new THREE.WebGLRenderer()
renderer.setSize(window.innerWidth, window.innerHeight);
let c = document.getElementById('c')

c.appendChild(renderer.domElement);


renderer.setClearColor(0xaaaaaa)    // 设置背景色
renderer.shadowMap.enabled = true   // 开启投影

function makeCamera(fov = 40) {
    const aspect = 2
    const zNear = 0.1
    const zFar = 1000
    return new PerspectiveCamera(fov,aspect,zNear,zFar)
}

const camera = makeCamera()
camera.position.set(16,8,20)
camera.lookAt(0,0,0)

// 设置主光
const light = new THREE.DirectionalLight(0xffffff,1) // 平行光 / 正交光
light.position.set(0,20,0)
scene.add(light)
light.castShadow = true // 允许呈现阴影
light.shadow.mapSize.width = 2048   // 设置阴影的分辨率
light.shadow.mapSize.height = 2048
const d = 50
// 设置主光的视域
light.shadow.camera.left = -d
light.shadow.camera.right = d
light.shadow.camera.top = d
light.shadow.camera.bottom = -d
light.shadow.camera.near = 1
light.shadow.camera.far = 50

// 设置辅助光(不投影)
{
    const light = new THREE.DirectionalLight(0xffffff,1)
    light.position.set(1,2,4)
    scene.add(light)
}

// 创建地面
const groundGeometry = new THREE.PlaneGeometry(50, 50)
const groundMaterial = new THREE.MeshPhongMaterial({ color: 0xcc8866 })
const groundMesh = new THREE.Mesh(groundGeometry, groundMaterial)
groundMesh.rotation.x = Math.PI * -0.5
groundMesh.receiveShadow = true
scene.add(groundMesh)

// 创建坦克
const tank = new THREE.Group()
scene.add(tank)
const carRadius = 1     // 坦克主体的半径
const bodyCenterY = (carRadius * 3) / 2  // 设置坦克身体中心
// 设置坦克相机
const tankCameraFov = 75
const tankCamera =  makeCamera(tankCameraFov)
tankCamera.position.y = 5
tankCamera.position.z = -10
tankCamera.lookAt(0,bodyCenterY,0)
tank.add(tankCamera)

// 绘制坦克车身
const bodyGeometry = new THREE.SphereGeometry(carRadius) // 绘制球体
const bodyMaterial = new THREE.MeshPhongMaterial({color:0x6688aa})
const bodyMesh = new THREE.Mesh(bodyGeometry,bodyMaterial)
bodyMesh.position.y = bodyCenterY
bodyMesh.castShadow = true // 允许投射阴影
tank.add(bodyMesh)

// 绘制坦克轮胎
const wheelRadius = 0.6 // 轮胎圆柱体的半径
const wheelThickness = 0.5 // 轮胎的厚度
const wheelSegments = 8  // 分划
const wheelGeometry = new THREE.CylinderGeometry(
    wheelRadius,
    wheelRadius,
    wheelThickness,
    wheelSegments
)
const wheelMaterial = new THREE.MeshPhongMaterial({
    color: 0x888888
})
const cx = carRadius + wheelThickness / 2   // 设置两个轮胎所处的位置
const wheelMeshes = [-cx,cx].map(x => {
    const mesh = new THREE.Mesh(wheelGeometry,wheelMaterial)
    mesh.rotation.z = Math.PI * 0.5     // 将轮胎立起来
    mesh.position.set(x,wheelRadius,0)
    mesh.castShadow = true
    tank.add(mesh)
    return mesh
})

// 创建坦克炮筒
const barrel = new THREE.Group()
barrel.position.y = bodyCenterY + 0.3
tank.add(barrel)
// 绘制炮筒模型
const barrelSize = 0.3
const barrelLenth = 5
const barrelGeometry = new THREE.BoxGeometry(barrelSize,barrelSize,barrelLenth)
const barrelMesh = new THREE.Mesh(barrelGeometry,bodyMaterial)
barrelMesh.position.z = barrelLenth / 2
barrelMesh.castShadow = true
barrel.add(barrelMesh)
// 添加炮管相机
const barrelCamera = makeCamera()
barrelCamera.position.y = 1.4
barrel.add(barrelCamera)

// 设置目标
const target = new THREE.Group()
target.position.z = 2
target.position.y = 4
scene.add(target)
// 设置浮动节点
const targetBob = new THREE.Group()
target.add(targetBob)
// 设置目标模型
const targetGeometry = new THREE.SphereGeometry(0.5,6,3) // 球体
const targetMaterial = new THREE.MeshPhongMaterial({
    color: 0x00ff00,
    flatShading: true // 平面着色,使其棱角分明
})
const targetMesh = new THREE.Mesh(targetGeometry,targetMaterial)
targetMesh.castShadow = true
targetBob.add(targetMesh)

// 设置目标相机
const targetCamera = makeCamera()
targetCamera.position.y = 1
targetCamera.position.z = -2
targetCamera.rotation.y = Math.PI
// 目标相机旋转轴
const targetCameraPivot = new THREE.Group()
targetBob.add(targetCameraPivot)
targetCameraPivot.add(targetCamera)

// 绘制坦克移动路径
const curve = new THREE.SplineCurve([
    new THREE.Vector2(-6,-2),
    new THREE.Vector2(-6,-8),
    new THREE.Vector2(8,0),
    new THREE.Vector2(3,8),
    new THREE.Vector2(-6,4),
    new THREE.Vector2(-6,-2),
])
const points = curve.getPoints(50)  // 生成点集
const geometry = new THREE.BufferGeometry().setFromPoints(points) // 根据点集生成缓冲区几何
const material = new THREE.LineBasicMaterial({color:0xff0000})
const splineObject = new THREE.Line(geometry,material)
splineObject.rotation.x = Math.PI * 0.5
splineObject.position.y = 0.05
scene.add(splineObject)

// 设置坦克位置
const tankPosition = new Vector2()
// 设置坦克朝向
const tankTarget = new Vector2()
// 设置目标位
const targetPosition = new Vector3()

// GUI切换相机
const gui = new dat.GUI({autoPlace: false})
const cameras = new Map([
    ['camera',camera],
    ['barrelCamera',barrelCamera],
    ['targetCamera',targetCamera],
    ['tankCamera',tankCamera]
])
const curCamera = {name: 'camera'}
gui.add(curCamera, 'name',[...cameras.keys()]).onChange(key => {
    const {
        domElement: {clientWidth,clientHeight} 
    } = renderer
    const cam = cameras.get(key)
    if(cam) {
        cam.aspect = clientWidth / clientHeight
        cam.updateProjectionMatrix()
    }

})



const info = document.getElementById('info')
info.appendChild(gui.domElement)

function render(time) {
    time *= 0.001
    const tankTime = time * 0.1 // 坦克移动多少
    curve.getPointAt(tankTime % 1,tankPosition)
    // 设置坦克位置
    tank.position.set(tankPosition.x,0,tankPosition.y)
    // 设置坦克朝向
    tank.lookAt(tankTarget.x,0,tankTarget.y)
    // 车轱辘的转动
    wheelMeshes.forEach(obj => {
        obj.rotation.x = time * 3
    })

    // 使目标对象上下浮动
    targetBob.position.y = Math.sin(time * 2) * 2

    // 获取目标点的世界位
    targetBob.getWorldPosition(targetPosition)
    // 炮筒指向目标点
    barrel.lookAt(targetPosition)


    let nowCamera = cameras.get(curCamera.name)
    if(curCamera.name === "targetCamera") {
        tank.getWorldPosition(targetPosition)
        targetCameraPivot.lookAt(targetPosition)
    }
    renderer.render(scene, nowCamera);
    requestAnimationFrame(render);
}



requestAnimationFrame(render)
