import * as THREE from "three"
// 导入轨道控制器
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls'
// 导入动画库
import gsap from "gsap";
// 导入dat.gui
import * as dat from "dat.gui";
// 目标:  透明纹理


// console.log(THREE)
// 1.构建场景
const scene = new THREE.Scene();
// 2.构建相机
const camera = new THREE.PerspectiveCamera(
    75,
    window.innerWidth / window.innerHeight,
    0.1,
    1000
);

// 设置相机位置
camera.position.set(10, 0, 0);
// camera.position.z = 0;
scene.add(camera);

//导入纹理
const textureLoader = new THREE.TextureLoader();
const doorColorTexture = textureLoader.load("./textures/door/R-C.jpg");
// texture纹理显示设置
doorColorTexture.minFilter = THREE.LinearFilter;
doorColorTexture.magFilter = THREE.LinearFilter;
// 添加物体
const cubeGeometry = new THREE.BoxGeometry(1, 1, 1); 
// 材质
const basicMaterial = new THREE.MeshBasicMaterial({
    color: 0xffff00,
    map: doorColorTexture
})
const cube = new THREE.Mesh(cubeGeometry,basicMaterial);
scene.add(cube);


// 窗体设置
function setgui() {
    const gui = new dat.GUI();
    // gui.add(cube.position,"x").min(0).max(5).step(0.01).name("移动x轴");
    const positionarr = ["x", "y", "z"];
    for (let i = 0; i < positionarr.length; i++) {
        gui.add(cube.position, positionarr[i])
            .min(0)
            .max(5)
            .name("移动" + positionarr[i] + "轴")
            .step(0.01)
            .onFinishChange((value) => {
                console.log("停下", value)
            });
    }
    // 修改物体的颜色
    const params = {
        color: "#ffff00",
        fn: () => {
            // 让立方体运动起来
            gsap.to(cube.position, { x: 5, duration: 5, yoyo: true, repeat: -1 })
        }
    }
    gui.addColor(params, "color").onChange((value) => {
        console.log("值被修改：", value);
        cube.material.color.set(value);
    })
    // 设置选项框
    gui.add(cube, "visible").name("是否显示");

    let folder = gui.addFolder("设置立方体");
    folder.add(cube.material, "wireframe")
    // 设置按钮点击触发某个事件
    folder.add(params, 'fn').name("立方体运动")
}
// setgui();







// 初始化渲染器
const renderer = new THREE.WebGLRenderer();
// 设置渲染的尺寸大小
renderer.setSize(window.innerWidth, window.innerHeight);
// 将webgl渲染的canvas内容添加到body
document.body.appendChild(renderer.domElement);

// 使用渲染器，通过相机将场景渲染进来
renderer.render(scene, camera);

// 创建轨道控制器
const controls = new OrbitControls(camera, renderer.domElement);

// 设置控制器阻尼，必须在动画循环里调用.update()
controls.enableDamping = true;


// 添加坐标轴辅助器
const axesHelper = new THREE.AxesHelper(5);
scene.add(axesHelper);
// 设置时钟
const clock = new THREE.Clock();


window.addEventListener('dblclick', () => {
    if (document.fullscreenElement) {
        document.exitFullscreen()
    } else {
        document.documentElement.requestFullscreen()
    }
})
function render(time) {
    controls.update();
    renderer.render(scene, camera);
    requestAnimationFrame(render); // 每一帧调用render函数进行重新渲染
}
render();

// 监听画面变化，更新渲染画面
window.addEventListener('resize', () => {
    // 更新摄像头
    camera.aspect = window.innerWidth / window.innerHeight; // 更新宽高比
    // 更新摄像机的投影矩阵
    camera.updateProjectionMatrix();

    // 更新渲染器
    renderer.setSize(window.innerWidth, window.innerHeight);
    //更新渲染器的像素比
    renderer.setPixelRatio(window.devicePixelRatio);
})
